package com.ofs.java.somu;


public class Trianglepattern1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        int i=1,j=0;
		while(i<=5)
         {
	      while(j<i)
	       {
		     System.out.print("* ");
		     j++;  
	       }
	      j=0;
	      i++;
	     System.out.println();
        }
      }
   }




